      @include media(sm) {
        height: 48rem;
        left: -10%;
      }
    }
    .large {
      @include media(sm) {
        display: none;
      }
      display: block;
    }
    .mobile {
      @include media(sm) {
        display: block;
      }
      display: none;
    }
  }
  &__right {
    padding: 0 2rem;
    @include media(sm) {
      grid-column: 1/13;
    }
    align-self: center;
    grid-column: span 6;
    h4 {
      font-weight: 700;
      padding: 2.5rem 0;
    }
    p {
      line-height: 1.5;
